import axios from 'axios'

import { 
    MARKET_GET_SUCCESS,
    MARKET_GET_FAILURE,
} from "./ActionTypes";


// Market Data GET 액션 생성자

export function getMarketRequest() {
    return (dispatch) => {
        return axios.get('/api/market')
        .then(res => res.data.postlist)
        .then(postlist => dispatch(getMarketSuccess(postlist)))
        .catch(err => {
            console.log(err)
            dispatch(getMarketFailure())
        })
    }
}

export function getMarketSuccess(postlist) {
    return {
        type: MARKET_GET_SUCCESS,
        postlist
    }
}

export function getMarketFailure() {
    return {
        type: MARKET_GET_FAILURE
    }
}
