import axios from 'axios';

import { 
    BOARD_GET_SUCCESS,
    BOARD_GET_FAILURE,
    BOARD_INIT,
} from "./ActionTypes";


// Board 초기화 액션 생성자

export function initiateBoard() {
    return {
        type: BOARD_INIT
    }
}


// Board Data GET 액션 생성자

export function getBoardRequest(boardName, scroll, search) {
    return (dispatch) => {

        /* search 데이터를 정제하는 과정(함수) 필요 */

        // GET 요청을 보낼 url
        // ex) '/api/market/?scroll=1', '/api/network/?scroll=2&q=전공책'
        const GETurl = `/api/${boardName}/?scroll=${scroll}${search?`&q=${search}`:''}`;    


        // GETurl로 GET 요청 보내기
        return axios.get(GETurl)

        // 성공하면, postlist 데이터를 가져와서 성공 액션을 dispatch 하고,
        .then(res => res.data.postlist)
        .then(postlist => {
            console.log(postlist)
            dispatch(getBoardSuccess(search, postlist))
        })

        // 실패하면, 실패 액션을 dispatch 한다.
        .catch(err => dispatch(getBoardFailure(err)))
    }
}

export function getBoardSuccess(search = '', postlist) {
    return {
        type: BOARD_GET_SUCCESS,
        search,
        postlist
    }
}

export function getBoardFailure(err) {
    return {
        type: BOARD_GET_FAILURE,
        err
    }
}
