//

// 상위 컴포넌트: pages/Market

import React, { Component } from "react";
import { connect } from 'react-redux';

// 컴포넌트
import MarketBoard from '../components/Market/MarketBoard';
import SearchIcon from "../components/Structure/SearchIcon";

// 액션
import { 
    getMarketRequest,
} from "../store/actions/market";

import {
    headerOn,
    headerOff,
    searchBarOn,
    categoryOff
} from "../store/actions/structure"

import { topicMarket } from "../store/actions/topic";


class MarketContainer extends Component {    

    componentDidMount() {
        this.props.headerOn();
        this.props.searchBarOn();
        this.props.categoryOff();
        this.props.getMarketRequest();
        this.props.topicMarket();
        window.addEventListener('scroll', this.onScroll);   // Scroll 이벤트가 생길 때, onScroll을 실행함
    }

    onScroll = (e) => {
        this.props.headerOff();
        if(document.documentElement.scrollTop <= 64){
            this.props.headerOn();
        }
    }

    render() {

        let {postlist} = this.props;

        return (
            <div>
                {
                    this.props.isGetSuccess ?
                    <MarketBoard postlist={postlist} /> :
                    'loading'
                }              
                <SearchIcon
                    isHeaderOn={this.props.isHeaderOn}
                    headerOn={this.props.headerOn}
                />  
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        postlist: state.market.postlist,
        isGetSuccess: state.market.isGetSuccess,
        isHeaderOn: state.structure.isHeaderOn
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        getMarketRequest: () => {dispatch(getMarketRequest())}, //
        headerOn: () => {dispatch(headerOn())},
        headerOff: () => {dispatch(headerOff())},
        searchBarOn: () => {dispatch(searchBarOn())},
        categoryOff: () => {dispatch(categoryOff())},

        topicMarket: () => {dispatch(topicMarket())},           // App의 topic state를 MARKET으로 설정
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(MarketContainer);
