// 상위 컴포넌트: SideBar

import React from "react";
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';

import './Menu.css';

const Menu = ({topicOrder}) => {

    const initDataList = [
        {categoryName: 'Market', isActive: false},
        {categoryName: 'Carpool', isActive: false},
        {categoryName: 'Network', isActive: false}
    ]

    let standardDataList = initDataList;

    if(topicOrder != -1){
        standardDataList[topicOrder].isActive = true;
    }

    return (
        <ul className={Menu}>
            {standardDataList.map((data, i) => {
                return(
                    <li className={data.isActive ? 'CategoryItem Active' : 'CategoryItem'}>
                        <Link to='/market' className='CategoryText'>Market</Link>
                    </li>
                )
            })}
        </ul>
    )
}

Menu.propTypes = {
    topicOrder: PropTypes.number // 하이라이트를 줄 topic의 인덱스
}

Menu.defaultProps = {
    topicOrder: -1
}


export default Menu;
