// 상위 컴포넌트: SideBarContainer.js


import React, { Component } from "react";
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';

import topicData from "./topics.json";

import Menu from "./Menu";

import './SideBar.css';


class SideBar extends Component {

    render() {

        return (
            <aside className='SideBar'>

                <div className="LogoImage">
                    <Link to='/'>유잇다 Logo</Link>
                </div>

                <Menu 
                    selededTopic={this.props.topic} 
                    topicData={topicData.upper}    
                />

                {/* <ul className='CategoryList'>
                    <li className='CategoryItem'><Link to='/market' className='CategoryText'>Market</Link></li>
                    <li className='CategoryItem'><Link to='/carpool' className='CategoryText'>Carpool</Link></li>
                    <li className='CategoryItem'><Link to='/network' className='CategoryText'>Networking</Link></li>
                </ul> */}

                <div className='BottomItemContainer'>

                    <Menu
                        selededTopic={this.props.topic} 
                        topicData={topicData.below}
                    />

                    {/* <ul className='CategoryList'>
                        <li className='CategoryItem'><Link to='/market' className='CategoryText'>Chatting</Link></li>
                        <li className='CategoryItem'><Link to='/carpool' className='CategoryText'>Manage</Link></li>
                    </ul> */}
                </div>

            </aside>
        )
    }
}

SideBar.propTypes = {
    topic: PropTypes.string.isRequired      // 하이라이트를 줄 topic의 인덱스
}


export default SideBar;
