// SideBar와 관련된 액션을 담은 컨테이너 컴포넌트

// 상위 컴포넌트: App

import React, { Component } from "react";
import { connect } from 'react-redux';

// 컴포넌트
import SideBar from "../components/Structure/SideBar";

// 액션
import { 
    HOME, MARKET, CARPOOL, NETWORK
} from "../store/actions/topic";


class SideBarContainer extends Component {    

    state = {
        topicOrder: -1
    }

    _topicToOrder = () => {
        switch(this.props.topic) {
            case HOME:
                return 0

            case MARKET:
                return 1

            case CARPOOL:
                return 2

            case NETWORK:
                return 3

            default:
                return -1
        }
    }

    componentDidMount() { 
        this.setState({
            topicOrder: this._topicToOrder()
        })
    }

    render() {
        return (
            <SideBar topicOrder={this.state.topicOrder}/>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        topic: state.topic.topic
    }
}

const mapDispatchToProps = (dispatch) => {
    return {}
}

export default connect(mapStateToProps, mapDispatchToProps)(SideBarContainer);
