//

// 상위 컴포넌트: pages/Board

import React, { Component } from "react";
import { connect } from 'react-redux';

// 컴포넌트
import Board from "../components/Board";
import SearchIcon from "../components/Structure/SearchIcon";

// 액션
import {
    headerOn,
    headerOff,
    searchBarOn,
} from "../store/actions/structure"

import { 
    InitateBoard,
    getBoardRequest
} from '../store/actions/board'

import { topicSelect } from "../store/actions/topic";

class BoardContainer extends Component {    

    state = {
        isGetSuccess: false
    }

    componentDidMount() {
        const { boardName, scroll, search } = this.props;

        this.props.headerOn();                                  // 헤더 On
        this.props.searchBarOn();                               // 검색창 On
        this.props.topicSelect(boardName);                      // app의 topic state를 boardName으로 설정

        // this.props.InitateBoard();                              // Board 초기화
        this.props.getBoardRequest(boardName, scroll, search);  // Board 데이터 GET 요청

        //this._getBoardRequest(boardName, scroll, search);

        window.addEventListener('scroll', this.onScroll);       // Scroll 이벤트가 생길 때, onScroll을 실행함
    }

    onScroll = (e) => {
        this.props.headerOff();
        if(document.documentElement.scrollTop <= 64){
            this.props.headerOn();
        }
    }

    _getBoardRequest = (boardName, scroll, search) => {
        this.props.getBoardRequest(boardName, scroll, search);
        this.setState({
            isGetSuccess: this.props.isGetSuccess
        })
    }

    componentWillReceiveProps(nextProps) {
        console.log(`receieve`)
        console.log(nextProps)
    }

    shouldComponentUpdate(nextProps) {
        if (this.props.boardName !== nextProps.boardName){
            return true;
        }
        return true;
    }


    render() {

        let {
            // properties
            boardName,
            postlist,
            search,
            isHeaderOn,
            isGetSuccess,

            // methods
            headerOn
        } = this.props;

        console.log(`render ${isGetSuccess}`);

        return (
            <div>
                {
                    isGetSuccess ?

                    <Board
                        boardName={boardName} 
                        postlist={postlist} 
                        search={search}
                    /> :
                    <div className='PageError'>
                        '새로고침을 눌러주세요 :)'
                    </div>
                }              
                <SearchIcon
                    isHeaderOn={isHeaderOn}
                    headerOn={headerOn}
                />  
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        isGetSuccess: state.board.isGetSuccess,     // GET 요청이 성공했는 지 여부
        postlist: state.board.postlist,             // postlist 데이터
        scroll: state.board.scroll,                 // 스크롤 횟수 (데이터를 받은 횟수)
        search: state.board.search,                 // 검색어 데이터
        isHeaderOn: state.structure.isHeaderOn,     // 헤더가 On 인지
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        headerOn: () => {dispatch(headerOn())},                     // 헤더를 나타나게 하는 메서드
        headerOff: () => {dispatch(headerOff())},                   // 헤더를 사라지게 하는 메서드
        searchBarOn: () => {dispatch(searchBarOn())},               // 검색바를 나타나게 하는 메서드

        InitateBoard: () => {dispatch(InitateBoard())},             // 보드 초기화 메서드 
        getBoardRequest: (boardName, scroll, search) => {           // 보드 GET 요청 메서드
            dispatch(getBoardRequest(boardName, scroll, search))
        },

        topicSelect: (topic) => {dispatch(topicSelect(topic))},     // App의 topic state를 topic 값으로 설정
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(BoardContainer);
