import axios from "axios";

import { 
    NETWORK_GET_SUCCESS,
    NETWORK_GET_FAILURE
} from "./ActionTypes";


// Network Data GET 액션 생성자

export function getNetworkRequest() {
    return (dispatch) => {
        return axios.get('/api/networking')
        .then(res => res.data.postlist)
        .then(postlist => dispatch(getNetworkSuccess(postlist)))
        .catch(err => {
            console.log(err)
            dispatch(getNetworkFailure())
        })
    }
}

export function getNetworkSuccess(postlist) {
    return {
        type: NETWORK_GET_SUCCESS,
        postlist
    }
}

export function getNetworkFailure() {
    return {
        type: NETWORK_GET_FAILURE
    }
}
